import React from 'react';
import "./ImageLinkForm.css"


const ImageLinkForm = ({onInputChange, onButtonSubmit, onReturn, boxes}) => {


   var total = boxes.length

   function hideTotal() {
       var T = document.getElementById("hide")
       T.style.display = "block";
   }

   function submitAndTotal (){
       onButtonSubmit();
       hideTotal();
   }
return (

        <div>

        <div>
        <p className="f3">{"This Magic Brain will detect faces in your pictures. Try it out!"}</p>

        <div className="center">
            <div className="center pa4 br3 shadow-5 form">
        <input className="f4 pa2 w-70 center" type="text" onChange={onInputChange} onKeyPress={onReturn}/>
        <button className="w-30 grow f4 link ph3 pv2 dib white bg-orange" onClick={submitAndTotal()}>Detect Face</button>

            </div>

        </div>



    </div>

    <p id="hide" className= "b f3" style={{color:"purple", display:"none"}}> {`${total} face(s) detected in this image.`}</p>


    </div>
    );
}

export default ImageLinkForm

