import React, { Component } from 'react'
import Navigation from './components/Navigation/Navigation.js'
import SignIn from './components/SignIn/SignIn.js'
import Register from './components/Register/Register.js'
import Logo from './components/Logo/Logo.js'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm.js';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js';
import Rank from './components/Rank/Rank.js';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import './App.css';


const app = new Clarifai.App({

    apiKey: "771a12bc39784c12ad9628465629a3ba"
});


const particlesOptions = {

        "particles": {
	        "number": {
	            "value": 100
	        },
	        "size": {
	            "value": 3
	        }
	    },
	    "interactivity": {
	        "events": {
	            "onhover": {
	                "enable": true,
	                "mode": "repulse"
	            }
	        }
	    }


}


class App extends Component {

    constructor(){

        super();
        this.state = {

            input: "",
            imageUrl:"",
            boxes: [],
            route: "SignIn",
            isSignedIn: false,
        }
    }



    calculateFaceLocation = (data) => {
        const image = document.getElementById("inputimage");
        const width = Number(image.width);
        const height = Number(image.height);

       return data.outputs[0].data.regions.map(face =>  {
        const clarifaiFace = face.region_info.bounding_box;

        return {
           leftCol: clarifaiFace.left_col * width, 
           topRow: clarifaiFace.top_row * height,
           rightCol: width - (clarifaiFace.right_col * width),
           bottomRow: height - (clarifaiFace.bottom_row * height)

                }

    });
}

    displayFaceBox = (boxes) => {
        const answer = boxes.reduce((acc, currentValue) => {
            return acc =+ 1
        })
        console.log(answer)
        // console.log(boxes)
        this.setState({boxes: boxes})

    }

    // showFacesDetected = (boxes) => {
    //     const answer = boxes.reduce((total, currentItem) => {
    //        return total + currentItem
    //     }, 0)
    //     console.log(answer)
    // }

    onInputChange = (event) => {

       this.setState({input:event.target.value}) 
    }




    onButtonSubmit = () => {

        this.setState({imageUrl: this.state.input});
        app.models
        .predict(
        Clarifai.FACE_DETECT_MODEL,
        this.state.input)
        .then((response) => {
            // console.log(response)
         this.displayFaceBox(this.calculateFaceLocation(response))
        })

        .catch((err) => {
         console.log(err);
        })



        };  

    onReturn = (event) => {

        if (event.key === "Enter") {
            this.onButtonSubmit()


         }

        }

    onRouteChange = (route) => {

        if (route === "signout") {

            this.setState({isSignedIn: false})


        } else if (route === "home"){
            this.setState({isSignedIn: true})

        } 

        this.setState({route: route});

    }




    render(){
        const {isSignedIn, imageUrl, route, boxes} = this.state;

  return (
    <div className="App">

   <Particles className="particles"
              params={particlesOptions}
            />

     <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
     { route === "home"  
     ? <div> 
     <Logo />
     <Rank />
     <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit} onReturn={this.onReturn} />
     <FaceRecognition boxes={boxes} imageUrl={imageUrl}/>
     </div> 

    : ( 
        route === "SignIn" ? <SignIn onRouteChange={this.onRouteChange}/>
       : route === "signout" ? <SignIn onRouteChange={this.onRouteChange}/>
       : <Register onRouteChange={this.onRouteChange}/>
    //    : <Register onRouteChange={this.onRouteChange}/>

  )

     }
    </div>
  );
}}

export default App;
