import React, { Component } from 'react'
import Navigation from './components/Navigation/Navigation.js'
import SignIn from './components/SignIn/SignIn.js'
import Register from './components/Register/Register.js'
import Logo from './components/Logo/Logo.js'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm.js';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js';
import Rank from './components/Rank/Rank.js';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import './App.css';


const app = new Clarifai.App({

    apiKey: "771a12bc39784c12ad9628465629a3ba"
});


const particlesOptions = {

        "particles": {
	        "number": {
	            "value": 100
	        },
	        "size": {
	            "value": 3
	        }
	    },
	    "interactivity": {
	        "events": {
	            "onhover": {
	                "enable": true,
	                "mode": "repulse"
	            }
	        }
	    }


}

const initialState = {

        input: "",
        imageUrl:"",
        boxes: [],
        route: "SignIn",
        isSignedIn: false,
        user: {
            id: "",
            name: "",
            email: "",
            // password: "",
            entries: 0,
            joined: ""

    }
}

class App extends Component {

    constructor(){
        super();
        this.state = initialState
        }



    loadUser = (data) => {
        this.setState({user: {
            id: data.id,
            name: data.name,
            email: data.email,
            // password: "",
            entries: data.entries,
            joined: data.joined,

        }}) 

    }





    calculateFaceLocation = (data) => {
        const image = document.getElementById("inputimage");
        const width = Number(image.width);
        const height = Number(image.height);

       return data.outputs[0].data.regions.map(face =>  {
        const clarifaiFace = face.region_info.bounding_box;

        return {
           leftCol: clarifaiFace.left_col * width, 
           topRow: clarifaiFace.top_row * height,
           rightCol: width - (clarifaiFace.right_col * width),
           bottomRow: height - (clarifaiFace.bottom_row * height)

                }

    });
}

    displayFaceBox = (boxes) => {
        console.log(boxes.length);
        this.setState({boxes: boxes})
        // const data = boxes.toString();

        }



    showFacesDetected = (boxes) => {
        var sum = 0;
        sum =+ boxes;
        console.log(sum);
    }

    onInputChange = (event) => {

       this.setState({input:event.target.value}) 
    }




    onButtonSubmit = () => {

        this.setState({imageUrl: this.state.input});
        app.models
        .predict(
        Clarifai.FACE_DETECT_MODEL,
        this.state.input)
        .then((response) => {
            // console.log(response)
         if (response) {
             fetch("http://localhost:3000/image", {
                method: "put",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify({
                  id: this.state.user.id
                })
            })
                .then(response => response.json())
                .then(count => {
                    this.setState(Object.assign(this.state.user, { entries: count}))
                })
                .catch(console.log)    
         }   
         this.displayFaceBox(this.calculateFaceLocation(response))
        })

        .catch((err) => {
         console.log(err);
        })



        };  

    onReturn = (event) => {

        if (event.key === "Enter") {
            this.onButtonSubmit()


         }

        }


    onRouteChange = (route) => {

        if (route === "signout") {

            this.setState(initialState)


        } else if (route === "home"){
            this.setState({isSignedIn: true})

        } 

        this.setState({route: route});

    }


    signInReturn = (event) => {
        if (event.key === "Enter") {
        console.log("the enter key was pressed!")

         }
    }





    render(){
        const {isSignedIn, imageUrl, route, boxes} = this.state;

  return (
    <div className="App">

   <Particles className="particles"
              params={particlesOptions}
            />

     <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
     { route === "home"  
     ? <div> 
     <Logo />
     <Rank name={this.state.user.name} entries={this.state.user.entries}/>
     <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit} onReturn={this.onReturn} />
     <FaceRecognition boxes={boxes} imageUrl={imageUrl}/>
     </div> 


    : ( 
        route === "SignIn" ? <div><SignIn loadUser={this.loadUser} onRouteChange={this.onRouteChange} signInReturn={this.signInReturn} /></div>
       : route === "signout" ? <SignIn onRouteChange={this.onRouteChange} loadUser={this.loadUser} signInReturn={this.signInReturn}/>
       : <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
    //    : <Register onRouteChange={this.onRouteChange}/>

  )

     }
    </div>
  );
}}

export default App;
